def pick_partial():
    return random.choice(partials)

def pick_non_terminal_partial():
    tps = [p for p in partials if not p.startswith("-")]
    return random.choice(tps)

def pick_terminal_partial():
    tps = [p for p in partials if p.startswith("-")]
    return random.choice(tps)

def is_terminal(s):
    return s.endswith("-")

def generateCreatureName_a1():
    w1 = pick_partial()
    while is_terminal(w1):
        w1 = w1 + pick_partial()
    w2 = pick_partial()
    while is_terminal(w2):
        w2 = w2 + pick_partial()
    return w1.title() + " " + w2.title()

def generateCreatureName_a2():
    return random.choice(completes).title() + " " + random.choice(completes).title()

def generateCreatureName():
    if random.randrange(0, 2):
        return generateCreatureName_a1()
    else:
        return generateCreatureName_a2()
